




declare module calcium {
    var web: web;
    var template: template;
    var logging: logging;
    var storage: storage;
}

declare module calcium.editor.routers {
    class ViewRouter extends calcium.web.Router {
        public storage: calcium.storage.Storage;
        constructor(storage: calcium.storage.Storage);
        public index(context: calcium.web.Context): void;
    }
}

declare module calcium.editor.routers {
    class StaticRouter extends calcium.web.Router {
        public path: string;
        public storage: calcium.storage.Storage;
        constructor(path: string, storage: calcium.storage.Storage);
        public index(context: calcium.web.Context): void;
    }
}

declare module calcium.editor.routers {
    class StorageRouter extends calcium.web.Router {
        public path: string;
        public storage: calcium.storage.Storage;
        constructor(path: string, storage: calcium.storage.Storage);
        public index(context: calcium.web.Context): void;
    }
}




declare module calcium.editor {
    class App extends calcium.web.App {
        public storage: calcium.storage.Storage;
        public public_directory: calcium.storage.Storage;
        public hub: calcium.web.Hub;
        constructor(storage: calcium.storage.Storage);
    }
}

