




declare module calcium {
    var web: web;
    var template: template;
    var logging: logging;
    var storage: storage;
}

declare module calcium.editor {
    class StaticRouter extends calcium.web.Router {
        public storage: calcium.storage.Storage;
        constructor(directory: string);
        public index(context: calcium.web.Context): void;
    }
    class EditorRouter extends calcium.web.Router {
        constructor();
        public index(context: calcium.web.Context): void;
    }
    class App extends calcium.web.App {
        public storage: calcium.storage.Storage;
        constructor(directory: string);
    }
}

