declare class razorjs
{
    views        : any;
    etags        : any;
    utils :
    {
        htmlString : Function;
        encode     : Function;
    };
    options : 
    {
        strict        : boolean;
        onerror       : Function;
        cacheDisabled : boolean
    };
    view         (id:string, page:string, callback?:{(data:string):void;}) : string;
    compile      (code:string, page:string): string;
    parse        (template:string) : {
        code     : string;
        sections : any [];
        helpers  : any [];
    };
    BasePage     : Function;
    HtmlHelper   : 
    {
        encode (str:string) : string;
        raw    (str:string) : string;
        renderPartial (view:any, model:any, page:any): string;
    };
    render       (markup:string, model:any, page:any, callback?:{(data:string):void;}) : string;
    findView     (viewName:string, callback?:{(data:string):void;}): string;
    getViewFile  (viewName:string): string;
    getViewEtag  (viewName:string): string;
    precompile   (code:string, page:string): string;
}