

declare module calcium {
    var logging: logging;
}




declare module calcium.web {
    class App {
        public server: http.Server;
        public routers: any;
        constructor();
        public listen(port: number): void;
        private locate_route(request);
        private handler(request, response);
        public error(code: number, request: http.ServerRequest, response: http.ServerResponse): void;
    }
}
declare module calcium.web {
    class Error {
    }
}



declare module calcium.web {
    class Context {
        public server: http.Server;
        public request: http.ServerRequest;
        public response: http.ServerResponse;
        public routers: any;
        public route: web.Route;
        public parameter: any;
    }
}

declare module calcium.web {
    class Route {
        public path: string;
        public method: string;
        public names: any;
        public regexp: RegExp;
        public callback: (app: web.Context) => void;
        constructor(path: string, method: string, callback: (app: web.Context) => void);
        public match(path: string): boolean;
        public parameter(path: string): any;
        private loadNames();
        private loadRegExp();
    }
}

declare module calcium.web {
    class Router {
        public routes: any;
        constructor();
        public match(method: string, path: string): web.Route;
        public get(path: string, callback: (app: web.Context) => void): void;
        public post(path: string, callback: (app: web.Context) => void): void;
        public put(path: string, callback: (app: web.Context) => void): void;
        public delete(path: string, callback: (app: web.Context) => void): void;
        public options(path: string, callback: (app: web.Context) => void): void;
    }
}





